import React, { Component } from 'react';
import { connect } from 'react-redux';
import * as actions from '../../../store/actions';
import { CRUD_ACTIONS } from '../../../utils';
import './UserRedux.scss';
import TableManageUser from './TableManageUser';
class UserRedux extends Component {
    constructor(props) {
        super(props);
        this.state = {
            genderArr: [],
            positionArr: [],
            roleArr: [],
            previewImg: '',
            isOpen: false,
            password: '',
            email: '',
            firstName: '',
            lastName: '',
            phonenumber: '',
            address: '',
            gender: '',
            position: '',
            role: '',
            avatar: '',

            action:''
        }
    }
    async componentDidMount() {
        this.props.getGenderStart();
        this.props.getPositionStart();
        this.props.getRoleStart();
    }
    componentDidUpdate(preProps, preState, snapshot) {
        if (preProps.genderRedux !== this.props.genderRedux) {
            let arrGenders = this.props.genderRedux
            this.setState({
                genderArr: arrGenders,
                gender: arrGenders && arrGenders.length > 0 ? arrGenders[0].key : ''
            })
        }
        if (preProps.roleRedux !== this.props.roleRedux) {
            let arrRoles = this.props.roleRedux

            

            this.setState({
                roleArr: arrRoles,
                role: arrRoles && arrRoles.length > 0 ? arrRoles[0].key : ''
            })
        }
        if (preProps.positionRedux !== this.props.positionRedux) {
            let arrPosition = this.props.positionRedux
            this.setState({
                positionArr: arrPosition,
                position: arrPosition && arrPosition.length > 0 ? arrPosition[0].key : ''
            })
        }
        if(preProps.listusers !== this.props.listusers){
            let arrGenders = this.props.genderRedux
            let arrRoles = this.props.roleRedux
            let arrPosition = this.props.positionRedux
            this.setState({
                password: '',
                email: '',
                firstName: '',
                lastName: '',
                phonenumber: '',
                address: '',
                gender: arrGenders && arrGenders.length > 0 ? arrGenders[0].key : '',
                position: arrPosition && arrPosition.length > 0 ? arrPosition[0].key : '',
                role: arrRoles && arrRoles.length > 0 ? arrRoles[0].key : '',
                avatar: '',
                action: CRUD_ACTIONS.CREATE
            })
        }
    }
    handleOnchangeImage = (e) => {
        let data = e.target.files;
        let file = data[0];
        if (file) {
            let objectUrl = URL.createObjectURL(file)
            this.setState({
                previewImg: objectUrl,
                avatar: file
            })
        }
    }
    handleSaveUser = () => {
        let isValid = this.checkValidateInput();
        if (isValid === false) return;
        this.props.createNewUser({
            email: this.state.email,
            password: this.state.password,
            firstName: this.state.firstName,
            lastName: this.state.lastName,
            address: this.state.address,
            phonenumber: this.state.phonenumber,
            gender: this.state.gender,
            roleId: this.state.role,
            positionId: this.state.position
        })
    }

    checkValidateInput = () => {
        let isValid = true;
        let arrCheck = ['email', 'password', 'firstName', 'lastName', 'phonenumber', 'address']
        for (let i = 0; i < arrCheck.length; i++) {
            if (!this.state[arrCheck[i]]) {
                isValid = false;
                alert('nhap day du thong tin')
                break
            }
        }
        return isValid
    }

    onChangeInput = (e, id) => {
        let copyState = { ...this.state }
        copyState[id] = e.target.value;
        this.setState({
            ...copyState
        })
    }
    handleEditUserFromParent = (user) =>{
        this.setState({
            password: 'hashcode',
            email: user.email,
            firstName: user.firstName,
            lastName: user.lastName,
            phonenumber: user.phonenumber,
            address: user.address,
            gender: user.gender,
            position: user.positionId,
            role: user.roleId,
            avatar: '',
            action: CRUD_ACTIONS.EDIT
        })
    }
    render() {
        let genders = this.state.genderArr;
        let positions = this.state.positionArr;
        let roles = this.state.roleArr;
        let { password, email, firstName, lastName, phonenumber, address, gender, position, role, avatar } = this.state
        let isGetGenders = this.props.isLoadingGender
        return (
            <div className='user-redux-container'>
                <div className="title" >
                    UserRedux vsgdsfdsjhs
                </div>
                <div>
                    {isGetGenders === true ? 'Loading Gender' : ''}
                </div>
                <div className='user-redux-body'>
                    <div className='container'>
                        <div className='row'>
                            <div className='col-12 mt-3'>Them moi nguoi dung</div>
                            <div className='col-3'>
                                <label>Email</label>
                                <input className='form-control' type='email'
                                    value={email}
                                    onChange={(e) => { this.onChangeInput(e, 'email') }}
                                />
                            </div>
                            <div className='col-3'>
                                <label>Password</label>
                                <input className='form-control' type='password'
                                    value={password}
                                    onChange={(e) => { this.onChangeInput(e, 'password') }}
                                />
                            </div>
                            <div className='col-3'>
                                <label>FirstName</label>
                                <input className='form-control' type='text'
                                    value={firstName}
                                    onChange={(e) => { this.onChangeInput(e, 'firstName') }}
                                />
                            </div>
                            <div className='col-3'>
                                <label>LastName</label>
                                <input className='form-control' type='text'
                                    value={lastName}
                                    onChange={(e) => { this.onChangeInput(e, 'lastName') }}
                                />
                            </div>
                            <div className='col-3'>
                                <label>Address</label>
                                <input className='form-control' type='text'
                                    value={address}
                                    onChange={(e) => { this.onChangeInput(e, 'address') }}
                                />
                            </div>
                            <div className='col-3'>
                                <label>PhoneNumber</label>
                                <input className='form-control' type='text'
                                    value={phonenumber}
                                    onChange={(e) => { this.onChangeInput(e, 'phonenumber') }}
                                />
                            </div>
                            <div className='col-3'>
                                <label>Gender</label>
                                <select className='form-control'
                                    onChange={(e) => { this.onChangeInput(e, 'gender') }}
                                    value={gender}
                                >
                                    {genders && genders.length > 0 &&
                                        genders.map((item, index) => {
                                            return (
                                                <option
                                                    key={index}
                                                    value={item.key}
                                                >
                                                    {item.valueVi}
                                                </option>
                                            )
                                        })}
                                </select>
                            </div>
                            <div className='col-3'>
                                <label>POSITION</label>
                                <select className='form-control'
                                    onChange={(e) => { this.onChangeInput(e, 'position') }}
                                    value={position}

                                >
                                    {positions && positions.length > 0 &&
                                        positions.map((item, index) => {
                                            return (
                                                <option
                                                    key={index}
                                                    value={item.key}
                                                >
                                                    {item.valueVi}
                                                </option>
                                            )
                                        })}
                                </select>
                            </div>
                            <div className='col-6'>
                                <label>roleId</label>
                                <select className='form-control'
                                    onChange={(e) => { this.onChangeInput(e, 'role') }}
                                    value={role}
                                    
                                >
                                    {roles && roles.length > 0 &&
                                        roles.map((item, index) => {
                                            return (
                                                <option
                                                    key={index}
                                                    value={item.key}
                                                >
                                                    {item.valueVi}
                                                </option>
                                            )
                                        })}
                                </select>
                            </div>
                            <div className='col-6'>
                                <label>Image</label>
                                <div className='preview-img-container'>
                                    <input id='previewImg' type='file' hidden
                                        onChange={(e) => this.handleOnchangeImage(e)}
                                    />
                                    <label className='label-upload' htmlFor='previewImg'> Tai Anh <i className='fas fa-upload'></i></label>
                                    <div className='preview-image' style={{ backgroundImage: `url(${this.state.previewImg})` }}>

                                    </div>
                                </div>
                            </div>
                            <div className='col-12 mt-3'>
                                <button className={this.state.action === CRUD_ACTIONS.EDIT ? 'btn btn-warning' : 'btn btn-primary'}
                                    onClick={() => this.handleSaveUser()}
                                >
                                    {this.state.action === CRUD_ACTIONS.EDIT ? 'Save Change' : 'Save'}
                                </button>
                            </div>
                            <div className='col-12 mb-5'>
                                <TableManageUser
                                    handleEditUserFromParentKey={this.handleEditUserFromParent}
                                        action={this.state.action}
                                />

                            </div>
                        </div>
                    </div>
                </div>
            </div>
        )
    }

}

const mapStateToProps = state => {
    return {
        genderRedux: state.admin.genders,
        positionRedux: state.admin.positions,
        roleRedux: state.admin.roles,
        listusers: state.admin.users,
        isLoadingGender: state.admin.isLoadingGender
    };
};

const mapDispatchToProps = dispatch => {
    return {
        getGenderStart: () => { dispatch(actions.fetchGenderStart()) },
        getPositionStart: () => { dispatch(actions.fetchPositionStart()) },
        getRoleStart: () => { dispatch(actions.fetchRoleStart()) },
        createNewUser: (data) => dispatch(actions.createNewUser(data)),
        fetUserRedux: () => dispatch(actions.fetchAllUserStart())

    };
};

export default connect(mapStateToProps, mapDispatchToProps)(UserRedux);
