import React, { Component } from "react";
import { connect } from "react-redux";
import { dispatch } from "../../../../redux";
import './ManageWorkshift.scss';
import * as actions from '../../../../store/actions';
import { CRUD_ACTIONS, CommonUtils } from '../../../../utils';
import DatePicker from "../../../../components/Input/DatePicker";
import Select from 'react-select'
class ManageWorkshift extends Component {
    constructor(props) {
        super(props)
        this.state = {
            selectedDoctor:'',
            listDoctors:[],
            hasOldData: false
        }
    }
    componentDidMount(){
        this.props.fetAllDoctorsRedux();
    }
    buildDataInputSelect = (inputData) =>{
        let result = [];
        if(inputData && inputData.length > 0 ) {
            inputData.map((item,index) =>{
                let object ={};
                object.label = `${item.lastName} ${item.firstName}`;
                object.value = item.id;
                result.push(object)
            })
        }
        return result
    }
    componentDidUpdate(prevProps,prevState,snapshot){
      if(prevProps.allDoctors !== this.props.allDoctors){
        let dataSelect = this.buildDataInputSelect(this.props.allDoctors)  
        this.setState({
              listDoctors: dataSelect
          })
      }
    } 
    handleChange = async (selectedDoctor) =>{
        this.setState({
            selectedDoctor
        })
        let res = await getDetailInfoDoctor(selectedDoctor.value);
        if(res && res.infor.errCode === 0 && res.infor.data && res.infor.data.kMarkdown){
            let markdown = res.infor.data.kMarkdown;
            this.setState({
                contentHTML: markdown.contentHTML,
                contentMarkdown: markdown.contentMarkdown,
                description: markdown.description,
                hasOldData: true
            })
            console.log('check res',this.state.contentMarkdown)
        } else{
            this.setState({
                contentHTML: '',
                contentMarkdown: '',
                description: '',
                hasOldData: false

            })
        }
    }
    handleOnChangeDesc = (e) =>{
        this.setState({
            description: e.target.value
        })
    }
    handleOnchageDatePicker = () => {

    }
    render() {
        return (
            <div className='user-redux-container'>
                <div className="title" >
                    GIO KHAM BENH BAC SI
                </div>
                <div className='user-redux-body'>
                    <div className='container'>
                        <div className='row'>
                            <div className='col-4'>
                                <label>Bac si</label>
                                <Select
                                    value={this.state.selectedDoctor}
                                    onChange={this.handleChange}
                                    options={this.state.listDoctors}
                                />
                            </div>
                            <div className='col-4'>
                                <label>Gio Kham benh</label>
                                <input className='form-control' type='email'
                                />
                            </div>
                            <div className='col-4'>
                                <label>Ngay Kham benh</label>
                                <DatePicker
                                    onChange={this.handleOnchageDatePicker}
                                    className='form-control'
                                />
                            </div>
                            <div className='col-12 mt-3'>
                                <button className='btn btn-primary'
                                >
                                    save
                                </button>
                            </div>
                            <div className='col-12 mb-5'>

                            </div>
                        </div>
                        <div className="row doctor-workshift-content ">
                            <div className="col-12">
                                <p><b> Cac Khung Gio</b></p>
                                <table id="customers">
                                    <tbody>
                                        <tr >
                                            <th>Ma</th>
                                            <th>Khung Gio</th>
                                            <th>Tac vu</th>

                                        </tr>
                                        <tr>
                                            <td>h2326</td>
                                            <td>2:00PM - 3:00PM</td>
                                            <td>
                                                <button className='btn-edit'><i className='fas fa-pencil-alt'></i></button>
                                                <button className='btn-delete' ><i className='fas fa-trash'></i></button>

                                            </td>
                                        </tr>
                                    </tbody>
                                </table>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        )

    }
}
const mapStateToProps = state => {
    return {
        isLoggedIn: state.user.isLoggedIn,
        allDoctors: state.admin.allDoctors
    };
};
const mapDispatchToProps = dispatch => {
    return {
        fetAllDoctorsRedux: () => dispatch(actions.fetchAllDoctor()),
        saveDetailDoctorRedux: (data) => dispatch(actions.saveDetailInfoDoctor(data))
    };
}
export default connect(mapStateToProps, mapDispatchToProps)(ManageWorkshift)
