import React, { Component } from 'react';
import { connect } from 'react-redux';
import './TableManageUser.scss'
import * as actions from '../../../store/actions'
class TableManageUser extends Component {
    constructor(props) {
        super(props)
        this.state = {
            usersRedux:[]
        }
    }
    componentDidMount(){
        this.props.fetUserRedux()
    }
    componentDidUpdate(prevProps,preState,snapshot){
        if(prevProps.listusers !== this.props.listusers){
            this.setState({
                usersRedux: this.props.listusers
            })
        }
    }
    render() {
        console.log('listUSer',this.props.listusers)
        console.log('state',this.props.usersRedux)
        return (
            <table id="customers">
                <tbody>
                    <tr >
                        <th>Email</th>
                        <th>FirstName</th>
                        <th>LastName</th>
                        <th>Address</th>
                        <th>Actions</th>
                    </tr>
                    <tr>
                        <td>{`item.email`}</td>
                        <td>{`item.firstName`}</td>
                        <td>{`item.lastName`}</td>
                        <td>{`item.address`}</td>
                        <td>
                            <button className='btn-edit' onClick={() => this.handleEditUser()}><i className='fas fa-pencil-alt'></i></button>
                            <button className='btn-delete' onClick={() => this.handleDeleteUser()}><i className='fas fa-trash'></i></button>
                        </td>
                    </tr>
                </tbody>
            </table>
        );
    }

}

const mapStateToProps = state => {
    return {
        listusers: state.admin.users
    };
};

const mapDispatchToProps = dispatch => {
    return {
        fetUserRedux: () => dispatch(actions.fetchAllUserStart())
     };
};

export default connect(mapStateToProps, mapDispatchToProps)(TableManageUser);
