import React, { Component } from 'react';
import { Redirect } from 'react-router-dom';
import { connect } from 'react-redux';
import Slider from 'react-slick';
import * as actions from '../../../store/actions'
class OutStandingDoctor extends Component {

    constructor(props){
        super(props)
        this.state ={
            arrDoctors: []
        }
    }

    componentDidMount(){
        this.props.loadTopDotors();

    }
    componentDidUpdate(prevProps,prevState,snapshot){
        if(prevProps.topDoctorsRedux !== this.props.topDoctorsRedux){
            this.setState({
                arrDoctors: this.props.topDoctorsRedux
            })
        }
    }

    render() {
        let allDoctors = this.state.arrDoctors;
        allDoctors = allDoctors.concat(allDoctors).concat(allDoctors)
        return (
            <div className='section-share section-outstanding-doctor'>
               <div className='section-container'>
                   <div className='section-header'>
                       <span className='title-section'>Bác sĩ nổi bậc</span>
                       <button className='btn-section'>Xem thêm</button>
                   </div>
                   <div className='section-body'>
                        <Slider {...this.props.settings} >
                            {allDoctors && allDoctors.length > 0 
                            && allDoctors.map((item,index) =>{
                                return(
                                    <div className='section-customize' key ={index}>
                                        <div className='customize-border'>
                                            <div className='outer-bg'>
                                            <   div className='bg-image section-outstanding-doctor'/>
                                            </div>
                                            <div className='position text-center'>
                                                <div>Pho Giao Su, Tien si gif gif ddo</div>
                                                <div>Tieu hoa</div>
                                            </div>             
                                        </div>
                                    </div >         
                                )
                            })}
                        </Slider>
                   </div>
               </div>
           </div>
        );
    }

} 

const mapStateToProps = state => {
    return {
        isLoggedIn: state.user.isLoggedIn,
        topDoctorsRedux: state.admin.topDoctors

    };
};

const mapDispatchToProps = dispatch => {
    return {
        loadTopDotors: () => dispatch(actions.fetchTopDoctor())
    };
};

export default connect(mapStateToProps, mapDispatchToProps)(OutStandingDoctor);
