import React, { Component } from "react";
import { connect } from "react-redux";
import { dispatch } from "../../../../redux";
import './ManageWorkshift.scss';
import * as actions from '../../../../store/actions';
import { CRUD_ACTIONS,CommonUtils } from '../../../../utils';
import DatePicker from "../../../../components/Input/DatePicker";
class ManageWorkshift extends Component {
    render() {
        
            return (
                <div className='user-redux-container'>
                    <div className="title" >
                        THÊM MỚI GiO KHAM BENH
                    </div>
                    <div className='user-redux-body'>
                        <div className='container'>
                            <div className='row'>
                                <div className='col-6'>
                                    <label>Gio Kham benh</label>
                                    <input className='form-control' type='email'
                                    />
                                </div> 
                                <div className='col-6'>
                                    <label>Ngay Kham benh</label>
                                    <DatePicker
                                        onChange = {this.handleOnchageDatePicker}
                                        className='form-control'
                                    />
                                </div>                  
                                <div className='col-12 mt-3'>
                                    <button className={this.state.action === CRUD_ACTIONS.EDIT ? 'btn btn-warning' : 'btn btn-primary'}
                                        onClick={() => this.handleSaveUser()}
                                    >
                                        {this.state.action === CRUD_ACTIONS.EDIT ? 'Save Change' : 'Save'}
                                    </button>
                                </div>
                                <div className='col-12 mb-5'>
                                    
                                </div>
                            </div>
                            <div className="row doctor-workshift-content ">
                            <div className="col-12">
                                <p><b> Cac Khung Gio</b></p>
                                <table id="customers">
                                    <tbody>
                                        <tr >
                                            <th>Ma</th>
                                            <th>Khung Gio</th>
                                            <th>Tac vu</th>
    
                                        </tr>
                                        <tr>
                                            <td>h2326</td>
                                            <td>2:00PM - 3:00PM</td>
                                            <td>
                                            <button className='btn-edit'><i className='fas fa-pencil-alt'></i></button>
                                                <button className='btn-delete' ><i className='fas fa-trash'></i></button>
    
                                            </td>
                                        </tr>
                                    </tbody>
                                </table>
                            </div>
                        </div>
                        </div>
                    </div>
                </div>
            )
        
    }
}
const mapStateToProps = state => {
    return {
        isLoggedIn: state.user.isLoggedIn
    };
};
const mapDispatchToProps = dispatch => {
    return {

    };
}
export default connect(mapStateToProps, mapDispatchToProps)(ManageWorkshift)
