import React, { Component } from 'react';
import { connect } from 'react-redux';
import { push } from "connected-react-router";
import * as actions from "../../store/actions";
import './Login.scss';
import { FormattedMessage } from 'react-intl';
import {handleLogin} from '../../services/userService';
class Login extends Component {
    constructor(props) {
        super(props);
        this.state = {
            username:'',
            password:'',
            isShowpass: false,
            errMessage:''
        }
    }   
    handleOnChangeUserName = (e) => {
        this.setState({
            username : e.target.value
        })
    }
    handleOnChangePassWord = (e) =>{
        this.setState({
            password : e.target.value
        })
    }
    handleLogin = async () => {
        this.setState({
            errMessage:''
        })
        try {
            let data = await handleLogin(this.state.username,this.state.password);   
            if(data && data.errCode !== 0){
                this.setState({
                    errMessage: data.message
                })
               
            }
            if(data && data.errCode === 0 ){
                this.props.userLoginSuccess(data.user);
                console.log('kogin success')
            }
        } catch (error) {
            if(error.response){
                if(error.response.data){
                    this.setState({
                        errMessage: error.response.data.message
                    })               
                }
            }
            console.log(error)
        }

    }
    handleShowPass = () =>{
        this.setState({
            isShowpass: !this.state.isShowpass
        })
    }
    render() { 
        return (
         <div className='login-background'>
             <div className='login-container'>
                 <div className='login-content col-7'>
                    <div className='col-12 text-login'>LOGIN</div>
                    <div className='col-12 form-group login-input'>
                        {/* <label>UserName:</label>
                        <input 
                            type='text' 
                            className='form-control ' 
                            placeholder='enter your username'
                            value={this.state.username}
                            onChange={(e) => {this.handleOnChangeUserName(e)}}
                        /> */}
                        <div class="form">
                            <input 
                                type="text" 
                                id="email" 
                                className="form__input" 
                                autocomplete="off" 
                                placeholder=" "
                                value={this.state.username}
                                onChange={(e) => {this.handleOnChangeUserName(e)}}
                            />
                            <label for="email" class="form__label">Email</label>
                        </div>
                    </div>
                    <div className='col-12 form-group login-input'>
                        {/* <label>PassWord:</label>
                        <div className='custom-input-password' >
                            <input 
                                type={this.state.isShowpass ? 'text' : 'password'} 
                                className='form-control' 
                                placeholder='enter your password'
                                onChange={(e) => {this.handleOnChangePassWord(e)}}
                            />
                            <span onClick={()=>{this.handleShowPass()}}> 

                                <i className={ this.state.isShowpass ? 'far fa-eye' : 'far fa-eye-slash'}></i>
                            </span>
                        </div> */}
                        <div class="form">
                            <input 
                                type={this.state.isShowpass ? 'text' : 'password'}
                                id="password" 
                                className="form__input" 
                                autocomplete="off" 
                                placeholder=" "
                                value={this.state.username}
                                onChange={(e) => {this.handleOnChangePassWord(e)}}
                            />
                            {/* <span onClick={()=>{this.handleShowPass()}}> 
                                <i className={ this.state.isShowpass ? 'far fa-eye' : 'far fa-eye-slash'}></i>
                            </span> */}
                            <label for="email" class="form__label">PassWord</label>
                        </div>
                    </div>
                    <div className='col-12' style={{color:'red'}}>
                        {this.state.errMessage}
                    </div>
                    <div className='col-12 '> 
                        <button 
                            className='btn-login'
                            onClick={() => {this.handleLogin()}}
                        > 
                            Login                       
                        </button>
                    </div>
                    <div className='col-12'>
                        <span className='forgot-password'>Forgot your password ?</span>
                    </div>
                    <div className='col-12 text-center mt3'>
                        <span className='text-other-login'> Or login with</span>
                    </div>
                    <div className='col-12 social-login'>
                        <i className='fab fa-google-plus-g google'></i>
                        <i className='fab fa-facebook-f  facebook'></i>
                    </div>
                 </div>
                 <div className='login-intro col-5'>
                    <div className='col-12 text-login'>WELCOM TO BOOKING MEDICAL</div>
                    <div className=''> 
                        <button 
                            className='btn-login-intro'
                            onClick={() => {this.handleLogin()}}
                        > 
                            SIGN UP                       
                        </button>
                    </div>
                 </div>
             </div>
         </div>
        )
    }
}
 
const mapStateToProps = state => {
    return {
        language: state.app.language
    };
};

const mapDispatchToProps = dispatch => {
    return {
        navigate: (path) => dispatch(push(path)),
        // userLoginFail: () => dispatch(actions.adminLoginFail()),
        userLoginSuccess: (userInfo) => dispatch(actions.userLoginSuccess(userInfo))
    };
};

export default connect(mapStateToProps, mapDispatchToProps)(Login);
