import React, { Component } from 'react';
import { connect } from "react-redux";
import HomeHeader from '../../HomePage/HomeHeader';
import './Booking.scss'
import HomeFooter from '../../HomePage/Section/HomeFooter';
class Booking extends Component {

    constructor(props) {
        super(props);
        this.state = {
            detailDoctor: {}
        }
    }

    async componentDidMount() {
    }
    render() {
        return (
            <>
                <HomeHeader
                    isShowBanner={false} 
                />
                <div className='booking-container'>
                    <div className='row booking-intro'>
                        <span className='booking-intro-item'>
                            Xác nhận thông tin đặt lịch
                        </span>
                        <div className='border'></div>
                    </div>
                    <div className='row'>
                        <div className='col-4 booking-left-overlay'>
                            <div className='row booking-info-doctor'>
                                <div className='col-6 booking-info-doctor-avt'></div>
                                <div className='col-6 booking-info-doctor-desc'>
                                    <div className='booking-info-doctor-desc-name'>
                                        <span>BS. Nguyen Tho Lo</span>
                                        <p> Chuyen khoa than kinh</p>
                                    </div>
                                </div>
                            </div>
                            <div className='booking-info-booking'>
                                <div className='booking-info-booking-time'>
                                    <span>8:00AM - 10:00AM</span>
                                    <p>thu 7 ngay 10-10-2022</p>
                                </div>
                                <div className='booking-info-booking-price'>
                                    <span>500.000 VND</span>
                                </div>
                            </div>
                        </div>
                        <div className='col-8'>
                            <div className="row">
                                <div className="form-row">
                                    <div className="form-group col-md-12">
                                        <label for="inputEmail4">Email</label>
                                        <input type="email" disabled onChange={(e) => { this.handleOnChangInput(e, 'email') }} value={this.state.email} className="form-control" placeholder="Email" name="email"></input>
                                    </div>
                                    <div className="form-group col-md-12">
                                        <label for="inputEmail4">FirstName</label>
                                        <input type="text" onChange={(e) => { this.handleOnChangInput(e, 'firstName') }} value={this.state.firstName} className="form-control" placeholder="FirstName" name="firstName"></input>
                                    </div>
                                    <div className="form-group col-md-12">
                                        <label for="inputPassword4">Last Name</label>
                                        <input type="text" onChange={(e) => { this.handleOnChangInput(e, 'lastName') }} value={this.state.lastName} className="form-control" name="lastName" placeholder="lastname"></input>
                                    </div>
                                    <div className="form-group">
                                        <label for="inputAddress">Address</label>
                                        <input type="text" onChange={(e) => { this.handleOnChangInput(e, 'address') }} value={this.state.address} className="form-control" name="address" placeholder="1234 Main St"></input>
                                    </div>
                                    <div className="form-group col-md-12">
                                        <label for="inputCity">Phone number</label>
                                        <input type="text" className="form-control" name="phonenumber"></input>
                                    </div>
                                    <div className="form-group col-md-12">
                                        <label for="inputState">Sex</label>
                                        <select name="gender" className="form-control">
                                            <option value="1">Male</option>
                                            <option value="0">Female</option>
                                        </select>
                                    </div>

                                </div>
                            </div>
                        </div>
                    </div>
                </div>
                <HomeFooter />
            </>
        );
    }
}

const mapStateToProps = state => {
    return {
    };
};

const mapDispatchToProps = dispatch => {
    return {
    };
};

export default connect(mapStateToProps, mapDispatchToProps)(Booking);
