import React, { Component } from 'react';
import { connect } from 'react-redux';
import './HomeHeader.scss';
import { FormattedMessage } from 'react-intl';
import Login from '../Auth/Login';
import { withRouter } from 'react-router';

class HomeHeader extends Component {
    handleShowLoginPage = () =>{
        this.props.history.push(`/login`)
        
    }
    render() {
        return (
            <React.Fragment>
                <div className='home-header-container'>
                    <div className='home-header-content'>
                        <div className='left-content'>
                            {/* <i className='fas fa-bars'></i> */}
                            <div className='header-logo'></div>
                        </div>
                        <div className='center-content'>
                            <div className='child-content'>
                                <div><b>Chuyên Khoa</b></div>
                                <div className='sub-title'>TÌm bác sĩ theo chuyên khoa</div>
                            </div>
                            <div className='child-content'>
                                <div><b>Dịch vụ</b></div>
                                <div className='sub-title'>Chọn gói khám bệnh</div>
                            </div>
                            <div className='child-content'>
                                <div><b>Bác sĩ</b></div>
                                <div className='sub-title'>Chọn bác sĩ giỏi</div>
                            </div>
                            <div className='child-content'>
                                <div><b>Liên hệ</b></div>
                                <div className='sub-title'>Tìm kiếm thông tin cần thiết</div>
                            </div>
                        </div>
                        <div className='right-content'> 
                                <button 
                                    className='btn-login'
                                    onClick={() => {this.handleShowLoginPage()}}
                                >ĐĂNG NHẬP
                                
                                </button>
                        </div>
                    </div>
                </div>
                {this.props.isShowBanner === true && 
                    <div className='home-header-banner'>
                        <div className='content-up'>
                            <div className='title1'>NỀN TẢNG Y TẾ</div>
                            <div className='title2'>CHĂM SÓC SỨC KHỎE TOÀN DIỆN</div>
                            <div className='search'>
                                <i className='fas fa-search'></i>
                                <input type='text' placeholder='Tìm kiếm bác sĩ'/>
                            </div>
                        </div>
                        <div className='content-down'>
                            <div className='options'>
                                <div className='option-child'>
                                    <div className='icon-child'><i className="fas fa-user-md"></i></div>
                                    <div className='text-child'>Khám chuyên khoa</div>
                                </div>
                                <div className='option-child'>
                                    <div className='icon-child'><i className='far fa-hospital'></i></div>
                                    <div className='text-child'>Khám tổng quát</div>
                                </div>
                                <div className='option-child'>
                                    <div className='icon-child'><i class="fas fa-book-medical"></i></div>
                                    <div className='text-child'>Xét nghiệm y học</div>
                                </div>
                                <div className='option-child'>
                                    <div className='icon-child'><i className="fad fa-scalpel"></i></div>
                                    <div className='text-child'>Phẩu thuật chỉnh hình</div>
                                </div>
                                <div className='option-child'>
                                    <div className='icon-child'><i className="fas fa-address-card"></i></div>
                                    <div className='text-child'>Tư vấn trực tiếp</div>
                                </div>
                                <div className='option-child'>
                                    <div className='icon-child'><i className='far fa-hospital'></i></div>
                                    <div className='text-child'>Cơ sở hạ tầng</div>
                                    
                                </div>
                            </div>
                        </div>
                    </div>
                }
            </React.Fragment>
        );
    }

} 

const mapStateToProps = state => {
    return {
        isLoggedIn: state.user.isLoggedIn,
        language: state.app.language,
    };
    
};

const mapDispatchToProps = dispatch => {
    return {
    };
};

export default withRouter( connect(mapStateToProps, mapDispatchToProps)(HomeHeader));
