import React, { Component } from 'react';
import { FormattedMessage } from 'react-intl';
import { connect } from 'react-redux';
import { getAllCodeService } from '../../../services/userService';
import * as actions from '../../../store/actions'
class UserRedux extends Component {
    constructor(props){
        super(props);
        this.state= {
            genderArr: []
        }
    }
    async componentDidMount() {
        this.props.getGenderStart();
        // try {
        //     let res = await getAllCodeService('gender');
        //     if(res && res.errCode === 0){
        //         this.setState({
        //             genderArr: res.data
        //         })
        //     }
        //     console.log('kdhshd' ,res)
        // } catch (e) {
        //     console.log(e)
        // }
    }

    componentDidUpdate(preProps,preState,snapshot){
        if(preProps.genderRedux !== this.props.genderRedux){
            this.setState({
                genderArr: this.props.genderRedux
            })
        }
    }
    render() {
        let gender = this.state.genderArr;
        return (
            <div className='user-redux-container'>
                <div className="title" >
                    UserRedux vsgdsfdsjhs
                </div>
                <div>
                    {isGetGender}
                </div>
                <div className='user-redux-body'>
                    <div className='container'>
                        <div className='row'>
                            <div className='col-12 mt-3'>Them moi nguoi dung</div>
                            <div className='col-3'>
                                <label>Email</label>
                                <input className='form-control' type='email'/>
                            </div>
                            <div className='col-3'>
                                <label>Password</label>
                                <input className='form-control' type='password'/>
                            </div>
                            <div className='col-3'>
                                <label>FirstName</label>
                                <input className='form-control' type='text'/>
                            </div>
                            <div className='col-3'>
                                <label>LastName</label>
                                <input className='form-control' type='text'/>
                            </div>
                            <div className='col-3'>
                                <label>Address</label>
                                <input className='form-control' type='text'/>
                            </div>
                            <div className='col-3'>
                                <label>PhoneNumber</label>
                                <input className='form-control' type='text'/>
                            </div>
                            <div className='col-3'>
                                <label>Gender</label>
                                <select className='form-control'>
                                    {gender && gender.length > 0 && 
                                    gender.map((item,index) =>{
                                        return(
                                            <option>{item.valueVi}</option>
                                        )
                                    })}
                                </select>
                            </div>
                            <div className='col-3'>
                                <label>roleId</label>
                                <select className='form-control'>
                                    <option selected> choose....</option>
                                    <option >....</option>
                                </select>
                            </div>
                            <div className='col-6'>
                                <label>roleId</label>
                                <select className='form-control'>
                                    <option selected> choose....</option>
                                    <option >....</option>
                                </select>
                            </div>
                            <div className='col-6'>
                                <label>Image</label>
                                <input className='form-control' type='text'/>
                            </div>
                            <div className='col-12 mt-3'>
                                <button className='btn btn-primary'> Save</button>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        )
    }

}

const mapStateToProps = state => {
    return {
        genderRedux: state.admin.gender,
        isLoadingGender: state.admin.isLoadingGender
    };
};

const mapDispatchToProps = dispatch => {
    return {
        getGenderStart: ()=>{
            dispatch(actions.fetchGenderStart)
        }
        // processLogout: () => dispatch(actions.processLogout()),
        
    };
};

export default connect(mapStateToProps, mapDispatchToProps)(UserRedux);
